module.exports.name = "AllProposals";
module.exports.kind = "HoudiniQuery";
module.exports.hash = "6760d93f91462c46bd258ee7576133a1";

module.exports.raw = `query AllProposals {
  proposals(first: 100, orderBy: createdTimestamp, orderDirection: desc) {
    id
    ipfsHash
    creator
    executor {
      id
      authorized
      propositionThreshold
      votingDuration
    }
    targets
    values
    signatures
    calldatas
    withDelegatecalls
    startBlock
    endBlock
    governanceStrategy
    currentYesVote
    currentNoVote
    votes {
      id
      voter
      support
      votingPower
      timestamp
    }
    createdTimestamp
    executionTime
    initiatorQueueing
    initiatorExecution
    lastUpdateTimestamp
    lastUpdateBlock
    title
    shortDescription
    govContract
    totalPropositionSupply
    totalVotingSupply
    createdBlockNumber
    totalCurrentVoters
    aipNumber
    author
    discussions
  }
}
`;